  const mockOnEdit = vi.fn();
  
  const mockPositions: Position[] = [
    {
      id: '1',
      symbol: 'AAPL',
      shares: 10,
      buy_price: 150,
      current_price: 180,
      change_percent: 1.5,
      current_value: 1800,
      cost_basis: 1500,
      gain_loss: 300,
